 var express 	= require("express")
	app 		= express();
	bodyParser  = require("body-parser");
	Campground  = require("./models/campground");
	mongoose    = require("mongoose");
	seedDB		= require("./seeds");
	Comment     = require("./models/comment");
	passport    = require("passport");
	flash		= require("connect-flash")
	LocalStrategy = require("passport-local");
	User        = require("./models/user")
	methodOverride = require("method-override");

var commentRoutes    = require("./routes/comments");
	campgroundRoutes = require("./routes/campgrounds");
	indexRoutes      = require("./routes/index");


//console.log(process.env.DATABASEURL);

var url = process.env.DATABASEURL || "mongodb://localhost:27017/yelp_camp_v10";

mongoose.Promise = global.Promise;
//mongoose.connect("mongodb://tarun:tarun1234@ds125472.mlab.com:25472/yelpcamp_thetseffect",{ useNewUrlParser: true });
//mongoose.connect("mongodb://localhost:27017/yelp_camp_v10",{ useNewUrlParser: true });

mongoose.connect(url,{ useNewUrlParser: true });

app.use(bodyParser.urlencoded({extended: true}));
app.set("view engine","ejs");
app.use(express.static(__dirname + "/public")); 
app.use(methodOverride("_method")); 
app.use(flash());
//seedDB();//Seed  the data base

//passport configuration
app.use(require("express-session")({
	secret:"hello fraands",
	resave: false,
	saveUninitialized: false
}));

app.use(passport.initialize());
app.use(passport.session());
passport.use(new LocalStrategy(User.authenticate()));
passport.serializeUser(User.serializeUser());
passport.deserializeUser(User.deserializeUser());
//middleware running on every singgle route passing the user info
app.use(function(req, res, next){
	res.locals.currentUser =  req.user;
	res.locals.error = req.flash("error");
	res.locals.success = req.flash("success");
	res.locals.info = req.flash("info");
	next();
});




